import { Component } from "react";
import magia from "../Json/magia.json";
import ItemList from "../components/item-list/item-list";
import "./Pages-Styles/magia.styles.css";
import SearchBox from "../components/search-box/search-box.component"
import Dropdown from "../components/dropdown/dropdownIt/dropdownIt";

class Magia extends Component{
    constructor(){
        super();
        this.state ={
          Magia: [],
          SearchBar: '',
          Tradycja: [],
          DropdownM: ""

        }
    };

    componentDidMount(){
        this.setState({
          Magia: magia,
          Tradycja: ["Światła", "Cieni", "Ognia"]
        })
    }
    
    onSearchChange = (event) => 
    {const SearchBar = event.target.value.toLowerCase();
    this.setState(
        () => {
        return{SearchBar};
        });
    }

    setSelected = (event) =>
    {const DropdownM = event.target.value.toLowerCase();
    this.setState(
        () => {
            return{DropdownM};
            });
    }

    render(){
        const {SearchBar, Magia} = this.state;
        const {onSearchChange, setSelected} = this;
    
        const NameFiler = Magia.filter((zaklęcie) => {
            return zaklęcie.nazwa.toLowerCase().includes(SearchBar);
            }); 
        
        
        
        return(
            <div className="Page">
                <div className="searchBoxBackground">
                    <SearchBox
                    onChangeHandler = {onSearchChange} 
                    className = {'search-box'} 
                    placeholder = {'Nazwa zaklęcia'}/>
                    <Dropdown
                    selected = {setSelected}
                    setSelected = {setSelected}
                    className="Drp"/>
                </div>
                <div className="Container">
                    {NameFiler.map(item =>{
                        return(
                            <ItemList Mlist={item}/>
                        );
                    })}
                </div>
            </div>
        )
    }
}

export default Magia;
